//intial state and action

import { ActionType } from "../constant/Action-type";

const initialState = {
  product: [],
};

const inital = {
  cart: [],
};

export const productReducer = (state = initialState, { type, payload }) => {
  switch (type) {
    case ActionType.SET_PRODUCTS:
      return { ...state.product, payload };
    default:
      return state;
  }
};
