import React, { useContext, useEffect, useState } from 'react'
import { Link } from 'react-router-dom'

import "./fruits.css"

import Login from '../Login/Login'
import Signup from '../Signup/Signup';
import { UserContext } from '../Context';

export default function Nav() {

    const[trigger,setTrigger]=useState(false);
    const[trigger2,setTrigger2]=useState(false);

    const name=useContext(UserContext);

    const signinHandle=()=>{
        setTrigger(true);


        setTrigger2(false);

    }

    const signupHandle=()=>{
        setTrigger2(true);
        setTrigger(false);
    }

  return (
    <>

            {name}

        <div className='nav-wrap'>
            <div className='nav-content'>

                <div className='title'>
                    <h1>Farm House</h1>
                </div>

                <div className='searchBar'>
                        <select name="location" id="location">
                            <option value="cbe">Coimbatore</option>
                            <option value="select">Select Location</option>
                        </select>
                        <form>
                            <input type='text' placeholder="Search Here !" />
                        </form>
                </div>

                <div className='Account'>
                    <h3>
                        <Link to="" onClick={signinHandle} >Signin</Link>
                    </h3>
                    <h3>
                        <Link to=""  onClick={signupHandle}>Signup</Link>
                    </h3>
                </div>
            </div>

        </div>
        <Login trigger={trigger} Trigger={setTrigger}></Login>
        <Signup trigger={trigger2} Trigger={setTrigger2}></Signup>


    </>
  )
}
