import React, { useState } from "react";
import { data } from "../Fruits/FruitsData";

import { sellerData } from "../Seller/DataSeller";
export default function Apple() {
  const arr = window.location.href.split("/");
  const named = arr[arr.length - 1];
  const [loding, setLoding] = useState(false);

  const fruitsDetail = [];

  useState(() => {
    async function setInterval() {
      for (let i = 0; i < sellerData.length; i++) {
        let len = sellerData[i].fruits.length;
        for (let j = 0; j < len; j++) {
          // if (sellerData[i].fruits[j].name.includes(named)) {
          //   console.log("fruitsDetail");
          // }
          if (sellerData[i].fruits[j].name.includes(named)) {
            fruitsDetail.push(sellerData[i]);
          }
        }
      }
      setLoding(true);
      console.log(fruitsDetail);
    }
    setInterval();
  }, []);

  return <>{named}</>;
}
