import React, { useState } from "react";
import { sellerData } from "../Seller/DataSeller";
import { fruitsData } from "../Fruits/Data";

export default function Apple() {
  const arr = window.location.href.split("/");
  const named = arr[arr.length - 1].toLowerCase();
  const [loding, setLoding] = useState(false);
  const [img, setImg] = useState();
  const fruitsDetail = [];

  const [value, setValue] = useState([]);
  const [final, setFinal] = useState();

  useState(() => {
    async function image() {
      for (let i = 0; i < fruitsData.length; i++) {
        if (fruitsData[i].name === named) {
          setImg(fruitsData[i].img);
          break;
        }
      }
    }

    async function setInterval() {
      for (let i = 0; i < sellerData.length; i++) {
        let len = sellerData[i].fruits.length;
        for (let j = 0; j < len; j++) {
          if (sellerData[i].fruits[j].name.toLowerCase().includes(named)) {
            fruitsDetail.push({
              name: sellerData[i].name,
              price: sellerData[i].fruits[j].price,
            });
          }
        }
      }
      setValue(fruitsDetail);

      setFinal("fruitsDetail");
    }
    setInterval();

    setLoding(true);
  }, []);

  // console.log(img);

  return (
    <>
      {/* <Nav /> */}

      <p> {named}</p>

      {/* {loding
        ? value.map((items) => (
            <div className="dealersDisplay">
              <p>{items.name}</p>
              <p>{items.fruits.map((p) => p.price)}</p>
            </div>
          ))
        : "bye"} */}

      {loding
        ? value.map((items) => (
            <div className="display-sellers-wrap">
              <h1>{items.name}</h1>
              <p>{items.price}</p>
            </div>
          ))
        : "jjh"}
    </>
  );
}
